import {
  Component,
  Input,
  OnInit,
  TemplateRef,
  ChangeDetectionStrategy,
  HostListener
} from '@angular/core';
import { switchMap } from 'rxjs/operators';
import { merge, Observable, startWith, map, filter, flatMap } from 'rxjs';
import { FormControl } from '@angular/forms';
import {QuizService} from '../quiz.service';

@Component({
  selector: 'app-autocomplete',
  template: `
              <div style="width: 230px">
                <input id="myInput"
                       [formControl]="control"
                       placeholder="Text">
                <ng-container *ngFor="let data of filteredData | async">
                  <button style="width: 100%; background-color: transparent;" (click)="categorySelected(data.id)" [innerHTML]="data.name | bold: control.value"></button>
                </ng-container>
              </div>
              `,
  styleUrls: ['./autocomplete.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  exportAs: 'appAutocomplete'
})
export class AutocompleteComponent implements OnInit{

 control = new FormControl();
 @Input() title?: string;
 @Input() listData?: Observable<any[]>;
 @Input() doesMatchSearch?: (item: any, search: string) => boolean;

 filteredData?: Observable<any[]>;
 tmp?: string[];

 @HostListener("document:click")
   clickedOut() {
     this.some_text = this.inside
            ? "Event Triggered"
            : "Event Triggered Outside Component";
          this.inside = false;
   }

 ngOnInit() {
  this.control.valueChanges.subscribe(x => {
     this.filteredData =  this.listData!.pipe(
       map(items => items.filter(item => item.name.toLowerCase().indexOf(x.toLowerCase()) > -1))
     )
   })
  }

  categorySelected(id: string){
    this.listData?.subscribe( res => {
         let cat =  res.find(x => x.id === Number(id)).name
         this.control.setValue(cat)
       }
      )
  }

}
